type Category = keyof typeof skillsData;

const categories: Category[] = ["languages", "frameworks", "tools"];

export function Skills() {
  const [activeCategory, setActiveCategory] = useState<Category>("languages");
  const [direction, setDirection] = useState(0);

  const handleCategoryChange = (newCategory: Category) => {
    const currentIndex = categories.indexOf(activeCategory);
    const newIndex = categories.indexOf(newCategory);
    setDirection(newIndex > currentIndex ? 1 : -1);
    setActiveCategory(newCategory);
  };
  
  const getCategoryDisplayName = (category: Category) => {
    if (category === 'tools') return 'Tools & Technologies';
    return category.charAt(0).toUpperCase() + category.slice(1);
  }

  const activeSkills = skillsData[activeCategory];
  const activeIndex = categories.indexOf(activeCategory);


  return (
    <section id="skills" className="bg-muted/50 py-20 dark:bg-card md:py-28 overflow-x-hidden">
      <div className="container mx-auto px-4">
        <h2 className="mb-12 text-center font-headline text-3xl font-bold md:text-4xl">
          Skills
        </h2>

        {/* Desktop Header */}
        <div className="hidden md:flex justify-center items-center mb-12 relative h-10 w-full max-w-2xl mx-auto">
          <div className="flex w-full items-center justify-center">
            {categories.map((category, index) => {
              const isActive = category === activeCategory;
              return (
                <motion.div
                  key={category}
                  className={cn(
                    "w-full text-center",
                     isActive ? "order-2" : (index < activeIndex ? "order-1" : "order-3")
                  )}
                  animate={{
                    flexGrow: isActive ? 1.5 : 1
                  }}
                  transition={{ type: "spring", stiffness: 300, damping: 30 }}
                >
                  <button
                    onClick={() => handleCategoryChange(category)}
                    className={cn(
                      "font-headline text-xl font-semibold transition-opacity duration-300",
                      isActive ? "opacity-100" : "opacity-40 hover:opacity-70"
                    )}
                  >
                    <motion.span
                      animate={{ scale: isActive ? 1.1 : 0.9 }}
                      transition={{ type: "spring", stiffness: 300, damping: 30 }}
                    >
                      {getCategoryDisplayName(category)}
                    </motion.span>
                  </button>
                </motion.div>
              );
            })}
          </div>
        </div>

        {/* Mobile Header */}
        <div className="md:hidden mb-8 text-center">
           <h3 className="font-headline text-2xl font-semibold">
              {getCategoryDisplayName(activeCategory)}
            </h3>
            <div className="flex justify-center gap-4 mt-2">
              {categories.map((cat) => (
                <button
                  key={cat}
                  onClick={() => handleCategoryChange(cat)}
                  className={cn(
                    "w-2 h-2 rounded-full",
                    activeCategory === cat ? "bg-primary" : "bg-muted-foreground/50"
                  )}
                  aria-label={`Go to ${getCategoryDisplayName(cat)}`}
                 />
              ))}
            </div>
        </div>

        <div className="relative h-80">
          <AnimatePresence custom={direction} initial={false}>
            <motion.div
              key={activeCategory}
              className="grid grid-cols-2 gap-6 md:grid-cols-4 lg:grid-cols-5 absolute w-full"
              custom={direction}
              initial={{ opacity: 0, x: direction > 0 ? "100%" : "-100%" }}
              animate={{ opacity: 1, x: 0 }}
              exit={{ opacity: 0, x: direction > 0 ? "-100%" : "100%" }}
              transition={{ duration: 0.3, ease: "easeInOut" }}
            >
              {activeSkills.map((skill) => (
                <SkillCard key={skill.name} name={skill.name} icon={skill.icon} />
              ))}
            </motion.div>
          </AnimatePresence>
        </div>
      </div>
    </section>
  );
}

const SkillCard = ({ name, icon }: { name: string; icon: React.ReactNode;}) => (
  <div className="flex transform flex-col items-center justify-center gap-2 rounded-lg border bg-card p-4 transition-transform duration-300 hover:-translate-y-1 hover:shadow-lg">
    <div className="h-12 w-12">{icon}</div>
    <p className="text-sm font-medium text-center">{name}</p>
  </div>
);

